mapper.select:
select dd.dept_name,group_name,dd.jobnumber,userid,dd.position,k_user.pay_place,k_user.leave_type,k_user.leave_reason_type,k_user.name,count(*),user_type,dispatch_factory,work_date, sum(go_work_day_Bai) go_work_day_Bai,sum(go_work_day_ye) go_work_day_ye,sum(go_work_day2) go_work_day2,sum(gongshi_base2) gongshi_base2,sum(jiaban2) jiaban2,sum(go_work_day_weekend2) go_work_day_weekend2,sum(jiaban_weekend2) jiaban_weekend2, sum(gongshi_base_zaotui) gongshi_base_zaotui,sum(gongshi_base_chidao) gongshi_base_chidao,sum(gongshi_base_kuang) gongshi_base_kuang, GROUP_CONCAT(distinct(form_component_value)) form_component_value,sum(form_nianjia) form_nianjia,sum(form_shijia) form_shijia,sum(form_bingjia) form_bingjia,sum(form_tiaoxiu) form_tiaoxiu,sum(form_chanjianjia) form_chanjianjia,sum(form_chanjia) form_chanjia,sum(form_peichanjia) form_peichanjia, sum(form_hunjia) form_hunjia,sum(form_lijia) form_lijia,sum(form_sangjia) form_sangjia,sum(form_burujia) form_burujia, sum(process_base_jiaban) process_base_jiaban,sum(process_weekend_jiaban) process_weekend_jiaban,sum(process_holiday_jiaban) process_holiday_jiaban, sum(buka_num) buka_num from k_day_detail dd inner join k_user on dd.userid=k_user.id where 1=1
and dd.userid =#{parMap.userId}
and dd.work_date like CONCAT(#{parMap.workDate},'%')
