mapper.select:
select dd.*, uu.name,uu.jobnumber,uu.position,uu.dept_name,uu.pay_place,uu.leave_type,uu.leave_reason_type from s_all dd inner join k_user uu on dd.userid=uu.id where dd.work_date like CONCAT(#{parMap.workDate},'%')
and(
uu.dept_ids like concat('%',#{item},'%')
or
uu.id = #{item}
)
and
uu.pay_place = #{item}
order by uu.dept_name,uu.jobnumber
limit #{parMap.pageNum},#{parMap.size}

select count(*) from s_all dd inner join k_user uu on dd.userid=uu.id where dd.work_date like CONCAT(#{parMap.workDate},'%')
and(
uu.dept_ids like concat('%',#{item},'%')
or
uu.id = #{item}
)
and
uu.pay_place = #{item}

update.foreach:
UPDATE s_all
go_work_day_bai = #{emp.goWorkDayBai}, go_work_day_ye = #{emp.goWorkDayYe}, go_work_day_yebu = #{emp.goWorkDayYebu}, go_work_day2 = #{emp.goWorkDay2}, gongshi_base2 = #{emp.gongshiBase2}, jiaban2 = #{emp.jiaban2}, go_work_day_weekend2 = #{emp.goWorkDayWeekend2}, jiaban_weekend2 = #{emp.jiabanWeekend2}, go_work_day_holiday_base = #{emp.goWorkDayHolidayBase}, jiaban_holiday_base = #{emp.jiabanHolidayBase}, go_work_day_holiday2 = #{emp.goWorkDayHoliday2}, jiaban_holiday2 = #{emp.jiabanHoliday2}, gongshi_base_zaotui = #{emp.gongshiBaseZaotui}, gongshi_base_chidao = #{emp.gongshiBaseChidao}, gongshi_base_kuang = #{emp.gongshiBaseKuang}, form_nianjia = #{emp.formNianjia}, form_shijia = #{emp.formShijia}, form_bingjia = #{emp.formBingjia}, form_tiaoxiu = #{emp.formTiaoxiu}, form_chanjianjia = #{emp.formChanjianjia}, form_chanjia = #{emp.formChanjia}, form_peichanjia = #{emp.formPeichanjia}, form_hunjia = #{emp.formHunjia}, form_lijia = #{emp.formLijia}, form_sangjia = #{emp.formSangjia}, form_burujia = #{emp.formBurujia}, process_base_jiaban = #{emp.processBaseJiaban}, process_weekend_jiaban = #{emp.processWeekendJiaban}, process_holiday_jiaban = #{emp.processHolidayJiaban},buka_num = #{emp.bukaNum}
WHERE id = #{emp.id}
