mapper.select:
select uadd.id,uadd.name,sum(money_add) addSum from s_oa_award aa inner join k_user uadd on uadd.jobnumber=aa.to_add_jobnumber where aa.work_date like CONCAT(#{parMap.workDate},'%') and
uadd.pay_place = #{item}
group by uadd.jobnumber

select uadd.id,uadd.name,sum(money_sub) subSum from s_oa_award aa inner join k_user uadd on uadd.jobnumber=aa.to_sub_jobnumber where aa.work_date like CONCAT(#{parMap.workDate},'%') and
uadd.pay_place = #{item}
group by uadd.jobnumber
